import { Outlet, Route, Routes } from 'react-router-dom'

import AuthenticationLayout from '~app/authentication.../layout'
import Authentication from '~app/authentication.../page'
import Signup from '~app/authentication.../signup/page'
import PasswordReset from '~app/authentication.../password-reset/page'
import Login from '~app/authentication.../login/page'

import NotFound from '~components/common/NotFound'

function AuthenticationSubRouter() {
  return (
    <Routes>
      <Route
        path=""
        element={<AuthenticationLayout><Outlet /></AuthenticationLayout>}
      >
        <Route
          index
          element={<Authentication />}
        />
        <Route
          path="signup"
          element={<Outlet />}
        >
          <Route
            index
            element={<Signup />}
          />
          <Route
            path="*"
            element={<NotFound />}
          />
        </Route>
        <Route
          path="password-reset"
          element={<Outlet />}
        >
          <Route
            index
            element={<PasswordReset />}
          />
          <Route
            path="*"
            element={<NotFound />}
          />
        </Route>
        <Route
          path="login"
          element={<Outlet />}
        >
          <Route
            index
            element={<Login />}
          />
          <Route
            path="*"
            element={<NotFound />}
          />
        </Route>
        <Route
          path="*"
          element={<NotFound />}
        />
      </Route>
    </Routes>
  )
}

export default AuthenticationSubRouter
